section.programlisting:
$ isql MASTER-IP:1111

-- and run the following commands:

-- enable this instance as a publisher
rdf_repl_start();

-- add graphs to replication list
rdf_repl_graph_ins('http://test.example.com');

$ cd database $ mkdir backup $ isql MASTER_IP:1111
-- and run the following commands:
-- clear any previous context backup_context_clear();
-- start the backup backup_online ('bkup-#', 1000000, 0, vector ('backup'));

$ bin/virtuoso-start.sh
$ isql SPARE-IP:1111

-- and run the following commands:

-- enable this instance as a publisher
rdf_repl_start();

-- add graphs to replication list
rdf_repl_graph_ins('http://test.example.com');

-- connect to master
repl_server ('MASTER', 'MASTER_DSN');

-- start subscribing to __rdf_repl
repl_subscribe ('MASTER', '__rdf_repl', 'dav', 'dav', 'dba', 'dba');

-- start initial replication
repl_sync_all ();

-- add subscription to scheduler
DB.DBA.SUB_SCHEDULE ('MASTER', '__rdf_repl', 1);

$ cd database $ mkdir backup $ isql SPARE_IP:1111
--and run the following commands:
-- clear any previous context backup_context_clear();
-- start the backup backup_online ('bkup-#', 1000000, 0, vector ('backup'));

$ bin/virtuoso-start.sh
$ isql FARM-1-IP:1111

-- connect to master
repl_server ('MASTER', 'MASTER_DSN');

-- start subscribing to __rdf_repl
repl_subscribe ('MASTER', '__rdf_repl', 'dav', 'dav', 'dba', 'dba');

-- start initial replication
repl_sync_all ();

-- add subscription to scheduler
DB.DBA.SUB_SCHEDULE ('MASTER', '__rdf_repl', 1);

$ bin/virtuoso-start.sh $ isql FARM-3-IP:1111
-- change replication name DB.DBA.REPL_SERVER_RENAME ('FARM-1', 'FARM-3')
-- sync against master repl_sync_all();

$ isql FARM-1-IP:1111

-- disable automatic checkpointing
checkpoint_interval (-1);

-- and do an explicit checkpoint
checkpoint;

$ bin/virtuoso-start.sh $ isql FARM-4-IP:1111
-- change replication name DB.DBA.REPL_SERVER_RENAME ('FARM-1', 'FARM-4')
-- sync against master repl_sync_all();

